import { useFavoritosContext } from "../context/FavoritosContext";

export default function Heart({ filled }) {

  const { verFavoritos } = useFavoritosContext();

  {favoritos.map((item) => (
    <img key={item.id}className="foto" src={item.imagen}  alt={item.desc}  />  
 ))}

  return (
    <>
      <button onClick={() => ( verFavoritos.id, item.src.tiny, item.alt)}>Like</button>
      <svg width="40px" viewBox="0 0 24 24">
        <path  fill={filled ? "red" : "red"}
          d="M12,21.35L10.55,20.03C5.4,15.36 2,12.27 2,8.5C2,5.41 4.42,3 7.5,3C9.24,3 10.91,3.81 12,5.08C13.09,3.81 14.76,3 16.5,3C19.58,3 22,5.41 22,8.5C22,12.27 18.6,15.36 13.45,20.03L12,21.35Z"
        />
      </svg>
    </>
  );
}
