import "../assets/css/galeria.css";
import { useEffect, useState } from "react";
import { useFavoritosContext } from "../context/FavoritosContext";

export default function Home() {
const [home, setHome] = useState([]);

const { addFavoritos } = useFavoritosContext();

const getData = async () => {
  const res = await fetch("fotos.json");
  const data = await res.json();
  setHome(data.photos);
};

useEffect(() => {  getData();}, []);

const [color, setColor] = useState('green');
 

function colorF ( color ) {
      if (color==='green')
       {setColor('red')}
      else
      {setColor('green')};
}
const seleccionado = (e) =>{
   
     if (e.target.backgroundColor == 'greem')
     {
      e.target.backgroundColor = 'red'
     }
     else     {
      e.target.backgroundColor = 'gree'
     }
       
}

       

return (
<div className="galeria  grid-columns-5 p-3 ">
        {home.map((item) => {
              return (
                    <div key={item.id} className="grid-container" >
                             <button  style={{ backgroundColor: color }} onClick={() => { addFavoritos( item.id, item.src.tiny, item.alt,"true") ; seleccionado(e) }}>Like</button>
                             <img   key={item.id}  src={item.src.tiny}  alt={item.alt}  /> 
 
                    </div>

              );
            })}
</div>

 
 
 
);}
