import "../assets/css/galeria.css";
import { useEffect, useState } from "react";
import { useFavoritosContext } from "../context/FavoritosContext";

export default function Home() {
const [home, setHome] = useState([]);

const { addFavoritos } = useFavoritosContext();

const getData = async () => {
  const res = await fetch("fotos.json");
  const data = await res.json();
  setHome(data.photos);
};

useEffect(() => {  getData();}, []);

function changeBackground(e) {
  if (e.target.style.background === 'red')
     {
      e.target.style.background = 'white';
      
     }
  else{e.target.style.background = 'red';}
 
}

const [color, setColor] = useState('red');

//<button onClick={() => addFavoritos( item.id, item.src.tiny, item.alt,"true")}>Like</button>
//<img onClick={() =>{ changeBackground  }} key={item.id}  src={item.src.tiny}  alt={item.alt}  /> 
//<button onClick={changeBackground}>Hover over me!</button>
return (
<div className="galeria  grid-columns-5 p-3 ">
        {home.map((item) => {
              return (
                    <div key={item.id} className="grid-container" >
                             <button  style={{ backgroundColor: {color} }} onClick={() => {addFavoritos( item.id, item.src.tiny, item.alt,"true")  }}>Like</button>
                             <img   key={item.id}  src={item.src.tiny}  alt={item.alt}  /> 
 
                    </div>

              );
            })}
</div>

 
 
 
);}
