import "../assets/css/galeria.css";
import { useEffect, useState } from "react";
import { useFavoritosContext } from "../context/FavoritosContext";

export default function Home() {
const [home, setHome] = useState([]);

const { addFavoritos } = useFavoritosContext();

const getData = async () => {
  const res = await fetch("fotos.json");
  const data = await res.json();
  setHome(data.photos);
};

const [color, setColor] = useState('');

useEffect(() => {  getData();}, []);

const seleccionado = (e) =>{
     let color1 = e.target.style.backgroundColor;
     if (color === 'white' || color === '')
     {
      e.target.style.backgroundColor  = 'red';
     }
     else     {
      e.target.style.backgroundColor = 'white';
     }
     color = e.target.style.backgroundColor;
     setColor(color);
     color = color;
}
   

return (
<div className="galeria  grid-columns-5 p-3 ">
        {home.map((item) => {
              return (
                    <div key={item.id} className="grid-container" >
                             <button  style="background-color: color)"  onClick={(e) => { addFavoritos( item.id, item.src.tiny, item.alt) ; seleccionado(e) }}>Like</button>
                             <img   key={item.id}  src={item.src.tiny}  alt={item.alt}  /> 
 
                    </div>

              );
            })}
</div>

 
 
 
);}
